import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super()

  }
  func1 = () => console.log('Você clicou no botão 1');
  func2 = () => console.log('Você clicou no botão 2');
  func3 = () => console.log('Você clicou no botão 3');
  render() {

    return (
      <div className="App">
        <button onClick={ func1 }>Botão 1</button>
        <button onClick={ func2 }>Botão 2</button>
        <button onClick={ func3 }>Botão 3</button>
      </div>
    );
  }
}

export default App;
