import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super()
    this.func1 = this.func1.bind(this);
    this.func2 = this.func2.bind(this);
    this.func3 = this.func3.bind(this);
  }
  func1() {
    console.log(this);
    console.log('Você clicou no botão 1');
  } 
  func2() {
    console.log(this);
    console.log('Você clicou no botão 2');
  } 
  func3() {
    console.log(this);
    console.log('Você clicou no botão 3');
  } 

  render() {

    return (
      <div className="App">
        <button onClick={ this.func1 }>Botão 1</button>
        <button onClick={ this.func2 }>Botão 2</button>
        <button onClick={ this.func3 }>Botão 3</button>
      </div>
    );
  }
}

export default App;
